ll modularExponentiation(ll base, ll expo)
{
  /*
  time O(n)
  space O(1) + recursion stack calls
  */
  static ll X = 1000000007;
  static ll res = 1;
  // Base case
  if (expo == 0)
    return res;
  if (expo % 2 == 1)
    res = ((base % X) * (res % X)) % X;
  base = ((base % X) * (base % X)) % X;
  expo /= 2;
  return modularExponentiation(base, expo);
}

template <typename T>
void print2D(const std::vector<std::vector<T>> &vec)
{
  for (const auto &row : vec)
  {
    for (const auto &elem : row)
    {
      std::cout << std::setw(1) << elem << " ";
    }
    std::cout << "\n";
  }
}

