int binaryExponentiation(int base, int expo)
{
  /*
    time O(n)
    space O(1) + recursion stack calls
  */
  static int res = 1;
  // Base case
  if (expo == 0)
    return res;
  // 3 ^ 9 = 3 ^ 8 * 3
  if (expo % 2 == 1)
    res *= base;
  // 3 ^ 8 = (3 ^ 4) * (3 ^ 4)
  base *= base;
  expo /= 2;
  return binaryExponentiation(base, expo);
}

