vi divisors(int n)
{
  vi divs;
  if (n == 0)
    return {};
  for (int i = 1; i * i <= n; ++i)
  {
    if (n % i == 0)
    {
      divs.push_back(i);
      if (i * i != n)
        divs.push_back(n / i);
    }
  }
  std::sort(all(divs));
  return divs;
}

bool is_prime(int n)
{
  if (n < 2)
    return false;
  for (int i = 2; i * i <= n; i++)
    if (n % i == 0)
      return false;
  return true;
}

vi prime_factorization(int n)
{
  vi factors;
  while (n % 2 == 0)
  {
    n /= 2;
    factors.push_back(2);
  }
  for (int i = 3; i * i <= n; i += 2)
  {
    while (n % i == 0)
    {
      n /= i;
      factors.push_back(i);
    }
  }
  if (n > 1)
    factors.push_back(n);
  return factors;
}

